        {
            if ((translationMask?.GetShouldTranslate((int)Repository_FieldIndex.Nickname) ?? true))
            {
                StringXmlTranslation.Instance.Write(
                    node: node,
                    name: nameof(item.Nickname),
                    item: item.Nickname,
