                    errorMask: errorMask);
            }
            if ((translationMask?.GetShouldTranslate((int)Repository_FieldIndex.Path) ?? true))
            {
                StringXmlTranslation.Instance.Write(
                    node: node,
                    name: nameof(item.Path),
                    item: item.Path,
                    fieldIndex: (int)Repository_FieldIndex.Path,
                    errorMask: errorMask);
            }
            if ((translationMask?.GetShouldTranslate((int)Repository_FieldIndex.AutoSync) ?? true))
            {
                BooleanXmlTranslation.Instance.Write(
                    node: node,
                    name: nameof(item.AutoSync),
                    item: item.AutoSync,
                    fieldIndex: (int)Repository_FieldIndex.AutoSync,
                    errorMask: errorMask);
            }
        }

        public void Write(
            XElement node,
            IRepositoryInternalGetter item,
            ErrorMaskBuilder errorMask,
            TranslationCrystal translationMask,
            string name = null)
        {
            var elem = new XElement(name ?? "HarmonizeGit.GUI.Repository");
            node.Add(elem);
            if (name != null)
            {
                elem.SetAttributeValue("type", "HarmonizeGit.GUI.Repository");
            }
            WriteToNodeXml(
                item: item,
                node: elem,
                errorMask: errorMask,
                translationMask: translationMask);
        }

        public void Write(
            XElement node,
            object item,
            ErrorMaskBuilder errorMask,
            TranslationCrystal translationMask,
            string name = null)
        {
            Write(
                item: (IRepositoryInternalGetter)item,
                name: name,
                node: node,
                errorMask: errorMask,
                translationMask: translationMask);
        }

        public void Write(
            XElement node,
            IRepositoryInternalGetter item,
            ErrorMaskBuilder errorMask,
            int fieldIndex,
            TranslationCrystal translationMask,
            string name = null)
        {
            try
            {
                errorMask?.PushIndex(fieldIndex);
                Write(
                    item: (IRepositoryInternalGetter)item,
                    name: name,
                    node: node,
                    errorMask: errorMask,
                    translationMask: translationMask);
            }
            catch (Exception ex)
            when (errorMask != null)
            {
                errorMask.ReportException(ex);
            }
            finally
            {
                errorMask?.PopIndex();
            }
        }

    }

