                    if ((translationMask?.GetShouldTranslate((int)CloneSpec_FieldIndex.ExplicitClones) ?? true))
                    {
                        try
                        {
                            errorMask?.PushIndex((int)CloneSpec_FieldIndex.ExplicitClones);
