                    this.ExplicitClones = (MaskItem<Exception, IEnumerable<MaskItem<Exception, Clone_ErrorMask>>>)obj;
                    break;
                default:
                    throw new ArgumentException($"Index is out of range: {index}");
            }
        }

        public bool IsInError()
        {
