        protected void ToString_FillInternal(FileGeneration fg)
        {
            fg.AppendLine("ExplicitClones =>");
            fg.AppendLine("[");
            using (new DepthWrapper(fg))
            {
                if (ExplicitClones.Overall != null)
                {
                    fg.AppendLine(ExplicitClones.Overall.ToString());
